    e_values = np.exp(values - np.max(values))
    return e_values / e_values.sum(axis=0)
def pattern_weaver(n, crystal_values, dimension):
    attention_scores = np.zeros((n, n))
    for i in range(n):
        for j in range(n):
            attention_scores[i][j] = np.dot(crystal_values[i], crystal_values[j])
    attention_scores = softmax(attention_scores)
    final_patterns = np.zeros((n, dimension))
    for i in range(n):
        for j in range(n):
            final_patterns[i] += attention_scores[i][j] * crystal_values[j]
    final_patterns = np.round(final_patterns, 4)
    return final_patterns.tolist()
assert pattern_weaver(5, [4, 2, 7, 1, 9], 1) == [8.9993, 8.9638, 9.0, 8.7259, 9.0]
assert pattern_weaver(3, [1, 3, 5], 1) == [4.7019, 4.995, 4.9999]
assert pattern_weaver(4, [2, 8, 6, 4], 1) == [7.9627, 8.0, 8.0, 7.9993]
assert pattern_weaver(3, [9, 2, 1], 1) == [9.0, 9.0, 8.9909]
assert pattern_weaver(3, [9, 2, 1], 2) == [9.0, 8.9996, 8.9233]

