from collections import Counter
def calculate_bm25_scores(corpus, query, k1=1.5, b=0.75):
    avgdl = sum([len(doc) for doc in corpus]) / len(corpus)
    inverted_index = {}
    for (i, doc) in enumerate(corpus):
        for word in doc:
            if word not in inverted_index:
                inverted_index[word] = [i]
            elif inverted_index[word][-1] != i:
                inverted_index[word].append(i)
    doc_word_counts = [Counter(doc) for doc in corpus]
    idf = {}
    for (word, doc_list) in inverted_index.items():
        idf[word] = math.log((len(corpus) - len(doc_list) + 0.5) / (len(doc_list) + 0.5))
    scores = []
    for (doc, word_counts) in zip(corpus, doc_word_counts):
        score = 0
        for word in query:
            if word in doc:
                tf = (k1 + 1) * word_counts[word] / (k1 * (1 - b + b * (len(doc) / avgdl)) + word_counts[word])
                score += idf[word] * tf
        scores.append(round(score, 3))
    return scores
assert calculate_bm25_scores([['the', 'cat', 'sat'], ['the', 'dog', 'ran'], ['the', 'bird', 'flew']], ['the', 'cat']) == [0.693, 0., 0. ]
assert calculate_bm25_scores([['the'] * 10, ['the']], ['the']) == [0,0]
assert calculate_bm25_scores([['term'] * 10, ['the'] * 2], ['term'], k1=1.0) == [.705, 0]

