import numpy as np
from collections import Counter
def meteor_score(reference, candidate, alpha=0.9, beta=3, gamma=0.5):

    def ngrams(sentence, n):
        sentence = sentence.lower().split()
        return list(zip(*[sentence[i:] for i in range(n)]))

    def precision(reference, candidate):
        common_ngrams = sum((Counter(candidate) & Counter(reference)).values())
        if len(candidate) == 0:
            return 0
        return common_ngrams / len(candidate)

    def recall(reference, candidate):
        common_ngrams = sum((Counter(candidate) & Counter(reference)).values())
        if len(reference) == 0:
            return 0
        return common_ngrams / len(reference)

    def harmonic_mean(precision, recall):
        if precision == recall == 0:
            return 0
        return 2 * precision * recall / (precision + recall)

    def penalty_fragmentation(reference, candidate):
        reference_length = len(reference)
        candidate_length = len(candidate)
        return np.exp(beta * (abs(candidate_length - reference_length) / reference_length))
    unigram_matches = Counter(ngrams(reference, 1)) & Counter(ngrams(candidate, 1))
    bigram_matches = Counter(ngrams(reference, 2)) & Counter(ngrams(candidate, 2))
    trigram_matches = Counter(ngrams(reference, 3)) & Counter(ngrams(candidate, 3))
    precision_unigram = sum(unigram_matches.values()) / len(candidate)
    precision_bigram = sum(bigram_matches.values()) / len(candidate)
    precision_trigram = sum(trigram_matches.values()) / len(candidate)
    recall_unigram = sum(unigram_matches.values()) / len(reference)
    recall_bigram = sum(bigram_matches.values()) / len(reference)
    recall_trigram = sum(trigram_matches.values()) / len(reference)
    fmean_unigram = harmonic_mean(precision_unigram, recall_unigram)
    fmean_bigram = harmonic_mean(precision_bigram, recall_bigram)
    fmean_trigram = harmonic_mean(precision_trigram, recall_trigram)
    penalty = penalty_fragmentation(reference, candidate)
    score = (fmean_unigram * precision_unigram * recall_unigram / 3 + fmean_bigram * precision_bigram * recall_bigram / 3 + fmean_trigram * precision_trigram * recall_trigram / 3) ** alpha * penalty
    return round(score, 3)
assert meteor_score('The dog barks at the moon', 'The dog barks at the moon') == 0.998
assert meteor_score('Rain falls gently from the sky', 'Gentle rain drops from the sky') == 0.625
assert meteor_score('The sun shines brightly', 'Clouds cover the sky') == 0.125
assert meteor_score('Birds sing in the trees', 'Birds in the trees sing') == 0.892

assert meteor_score("The cat sits on the mat", "The cat on the mat sits") == 0.938

