import numpy as np
def cramers_rule(A, b):
    if A.shape[0] != A.shape[1]:
        return -1
    if np.linalg.det(A) == 0:
        return -1
    A_unknowns = [A.copy() for _ in b]
    for (i, A_unknown) in enumerate(A_unknowns):
        A_unknown[:, i] = b
    det_A = np.linalg.det(A)
    x = [np.linalg.det(A_unknown) / det_A for A_unknown in A_unknowns]
    return np.around(x, decimals=4).tolist()
assert cramers_rule([[2, -1, 3], [4, 2, 1], [-6, 1, -2]], [5, 10, -3]) == [0.1667, 3.3333, 2.6667]
assert cramers_rule([[1, 2], [3, 4]], [5, 6]) == [-4.,4.5]
assert cramers_rule([[1, 2], [2, 4]], [3, 6]) == -1

def vector_sum(a: list[int | float], b: list[int | float]) -> list[int | float]:
    if len(a) != len(b):
        return -1
    else:
        return [x + y for (x, y) in zip(a, b)]
assert vector_sum([1, 2, 3], [4, 5, 6]) == [5, 7, 9]
assert vector_sum([1, 2], [1, 2, 3]) == -1
assert vector_sum([1.5, 2.5, 3.0], [2, 1, 4]) == [3.5, 3.5, 7.0]

