def k_fold_cross_validation(X: np.ndarray, y: np.ndarray, k=5, shuffle=True, random_seed=None):
    assert X.shape[0] == y.shape[0], 'X and y must have the same number of samples'
    if random_seed is not None:
        np.random.seed(random_seed)
    if shuffle:
        indices = np.random.permutation(X.shape[0])
    else:
        indices = np.arange(X.shape[0])
    fold_size = X.shape[0] // k
    res = []
    for fold_ind in range(k):
        start = fold_ind * fold_size
        end = (fold_ind + 1) * fold_size
        if fold_ind == k - 1:
            end = X.shape[0]
        test_indices = indices[start:end]
        train_indices = np.concatenate([indices[:start], indices[end:]])
        res.append({'train': train_indices, 'test': test_indices})
    return res

