def entropy(data):
    counts = Counter(data)
    ent = 0.0
    for count in counts.values():
        prob = count / len(data)
        ent += -prob * math.log2(prob)
    return ent
def information_gain(data, attr, target_attr):
    attr_values = set([record[attr] for record in data])
    total_entropy = entropy([record[target_attr] for record in data])
    weighted_entropy = 0.0
    for attr_value in attr_values:
        subset = [record for record in data if record[attr] == attr_value]
        attr_entropy = entropy([record[target_attr] for record in subset])
        weighted_entropy += len(subset) / len(data) * attr_entropy
    return total_entropy - weighted_entropy
