def learn_decision_tree(examples: list[dict], attributes: list[str], target_attr: str) -> dict:
    if len(examples) == 0:
        return {}
    if all((example[target_attr] == examples[0][target_attr] for example in examples)):
        return {'class': examples[0][target_attr]}
    if len(attributes) == 0:
        return {'class': most_common_value(examples, target_attr)}
    best_attr = max(attributes, key=lambda attr: information_gain(examples, attr, target_attr))
    tree = {'attribute': best_attr, 'children': {}}
