def gradient_descent(X, y, weights, learning_rate, n_iterations, batch_size=1, method='batch'):
    X = np.c_[np.ones(X.shape[0]), X]
    for i in range(n_iterations):
        if method == 'batch':
            gradients = 2 / X.shape[0] * X.T.dot(X.dot(weights) - y)
        elif method == 'stochastic':
            random_index = np.random.randint(X.shape[0])
            xi = X[random_index:random_index + 1]
            yi = y[random_index:random_index + 1]
            gradients = 2 / X.shape[0] * xi.T.dot(xi.dot(weights) - yi)
        elif method == 'mini-batch':
            random_indices = np.random.choice(X.shape[0], batch_size, replace=False)
            X_batch = X[random_indices]
            y_batch = y[random_indices]
            gradients = 2 / X_batch.shape[0] * X_batch.T.dot(X_batch.dot(weights) - y_batch)
        else:
            raise ValueError("Invalid method. Please use 'batch', 'stochastic', or 'mini-batch'.")
        weights = weights - learning_rate * gradients
    return weights.round(4).tolist()

