def rref(matrix):
    m = matrix.copy()
    (n_rows, n_cols) = m.shape
    lead = 0
    for r in range(n_rows):
        if n_cols <= lead:
            return m
        i = r
        while m[i][lead] == 0:
            i += 1
            if n_rows == i:
                i = r
                lead += 1
                if n_cols == lead:
                    return m
        (m[i], m[r]) = (m[r], m[i])
        if m[r][lead] != 0:
            m[r] = m[r] / m[r][lead]
        for i in range(n_rows):
            if i != r:
                m[i] = m[i] - m[r] * m[i][lead]
        lead += 1
    return m

