def gaussian_elimination(A, b):
    augmented_matrix = np.concatenate((A, b.reshape((-1, 1))), axis=1).astype(float)
    (num_rows, num_cols) = augmented_matrix.shape
    for i in range(num_rows):
        max_row_index = i + np.argmax(np.abs(augmented_matrix[i:, i]))
        if i != max_row_index:
            augmented_matrix[[i, max_row_index]] = augmented_matrix[[max_row_index, i]]
        augmented_matrix[i] = augmented_matrix[i] / augmented_matrix[i, i]
        for j in range(i + 1, num_rows):
            augmented_matrix[j] = augmented_matrix[j] - augmented_matrix[j, i] * augmented_matrix[i]
    x = np.zeros(num_rows)
    for i in reversed(range(num_rows)):
        x[i] = (augmented_matrix[i, -1] - np.dot(augmented_matrix[i, i + 1:-1], x[i + 1:])) / augmented_matrix[i, i]
