    avgdl = compute_avgdl()
    scores = []
    for document in corpus:
        score = 0
        for word in query:
            if word in document:
                tf = compute_tf(word, document)
                score += idf[word] * tf * (k1 + 1) / (tf + k1 * (1 - b + b * len(document) / avgdl))
        scores.append(score)
    return [round(score, 3) for score in scores]

import numpy as np
def compute_qkv(X, W_q, W_k, W_v):
    Q = np.dot(X, W_q)
    K = np.dot(X, W_k)
    V = np.dot(X, W_v)
    return (Q, K, V)
def self_attention(Q, K, V):
    dk = np.sqrt(K.shape[1])
    scores = np.dot(Q, K.T) / dk
    weights = np.exp(scores) / np.sum(np.exp(scores), axis=1, keepdims=True)
    return np.dot(weights, V)
def multi_head_attention(X, W_q, W_k, W_v, n_heads):
    (Qs, Ks, Vs) = ([], [], [])
    for i in range(n_heads):
        (W_q_i, W_k_i, W_v_i) = (W_q[i], W_k[i], W_v[i])
        (Q_i, K_i, V_i) = compute_qkv(X, W_q_i, W_k_i, W_v_i)
        Qs.append(Q_i)
        Ks.append(K_i)
        Vs.append(V_i)
    results = []
    for (Q_i, K_i, V_i) in zip(Qs, Ks, Vs):
        result = self_attention(Q_i, K_i, V_i)
        results.append(result)
    return np.around(np.asarray(results).tolist(), 4)

def phi_corr(x: list[int], y: list[int]) -> float:
    """
    Calculate the Phi coefficient between two binary variables.

    Args:
    x (list[int]): A list of binary values (0 or 1).
    y (list[int]): A list of binary values (0 or 1).

    Returns:
    float: The Phi coefficient rounded to 4 decimal places.
    """
    if len(x) != len(y):
        raise ValueError('The input lists must have the same length.')
    (xy_11, xy_10, xy_01, xy_00) = (0, 0, 0, 0)
    for i in range(len(x)):
        if x[i] == 1 and y[i] == 1:
            xy_11 += 1
        elif x[i] == 1 and y[i] == 0:
            xy_10 += 1
        elif x[i] == 0 and y[i] == 1:
            xy_01 += 1
        else:
            xy_00 += 1
    phi = (xy_11 * xy_00 - xy_10 * xy_01) / ((xy_11 + xy_10) * (xy_11 + xy_01) * (xy_01 + xy_00) * (xy_10 + xy_00)) ** 0.5
    return round(phi, 4)

