from collections import Counter
def normalize_and_tokenize(sentence):
    return sentence.lower().split()
def meteor_score(reference, candidate, alpha=0.9, beta=3, gamma=0.5):
    ref_words = normalize_and_tokenize(reference)
    cand_words = normalize_and_tokenize(candidate)
    ref_len = len(ref_words)
    cand_len = len(cand_words)
    match_count = 0
    trans_count = 0
    for ref_word in ref_words:
        if ref_word in cand_words:
            match_count += 1
            cand_words.remove(ref_word)
        else:
            trans_count += len(ref_word)
    precision = match_count / cand_len if cand_len != 0 else 0.0
    recall = match_count / ref_len if ref_len != 0 else 0.0
    f_mean = (1 - alpha) * precision * recall / (beta * precision + recall) if beta * precision + recall != 0 else 0.0
    penalty = gamma * ((cand_len - ref_len) / cand_len) if cand_len != 0 else 0.0
    score = f_mean * (1 - penalty)
    return round(score, 3)

