def orthonormal_basis(vectors: list[list[float]], tol: float=1e-10):
    basis = []
    for vector in vectors:
        v = np.array(vector)
        if basis:
            for b in basis:
                proj_vector = np.dot(v, b) * b
                v = v - proj_vector
        if np.linalg.norm(v) > tol:
            basis.append(v / np.linalg.norm(v))
    return [b.round(4).tolist() for b in basis]
