import numpy as np
def cramers_rule(A, b):
    if np.linalg.det(A) == 0:
        return -1
    n = len(b)
    x = np.zeros(n)
    for i in range(n):
        A_temp = np.copy(A)
        A_temp[:, i] = b
        det_A_temp = np.linalg.det(A_temp)
        x[i] = det_A_temp / np.linalg.det(A)
    return np.round(x, 4).tolist()

