import numpy as np
from scipy.special import softmax
def compute_policy_gradient(theta: np.ndarray, episodes: list[list[tuple[int, int, float]]]):
    grad_list = [np.zeros(theta.shape) for _ in range(len(episodes))]
    for (i, episode) in enumerate(episodes):
        T = len(episode)
        G = 0
        for t in reversed(range(T)):
            (state, action, reward) = episode[t]
            G += reward
            state_row = theta[state]
            probs = softmax(state_row)
            for a in range(len(probs)):
                if a == action:
                    grad_list[i][state, a] += (G - state_row.dot(probs)) * probs[a]
                else:
                    grad_list[i][state, a] += G * probs[a]
    avg_grad = np.mean(grad_list, axis=0)
    return avg_grad.round(4).tolist()

import numpy as np
def group_normalization(X: np.ndarray, gamma: np.ndarray, beta: np.ndarray, num_groups: int, epsilon: float=1e-05):
    (B, C, H, W) = X.shape
    X = X.reshape(B, num_groups, -1)
    mean = X.mean(axis=(2, 3), keepdims=True)
    var = X.var(axis=(2, 3), keepdims=True)
    X_norm = (X - mean) / np.sqrt(var + epsilon)
    X_norm = X_norm.reshape(B, C, H, W)
    output = gamma * X_norm + beta
    return np.round(output, 4).tolist()

import numpy as np
def find_treasure(start_x: float, learning_rate: float=0.1, tolerance: float=1e-06, max_iters: int=10000) -> float:

    def f(x):
        return x ** 4 - 3 * x ** 3 + 2

    def df(x):
        return 4 * x ** 3 - 9 * x ** 2
    x = start_x
    for _ in range(max_iters):
        gradient = df(x)
        if abs(gradient) < tolerance:
            break
        x -= learning_rate * gradient
    return round(x, 4)
