def svd_2x2_singular_values(A):

    def jacobi_rotation(A):
        if A[0][1] == 0:
            return A
        else:
            tetha = np.arctan(2 * A[0][1] / (A[0][0] - A[1][1])) / 2
            c = np.cos(tetha)
            s = np.sin(tetha)
            R = np.array([[c, -s], [s, c]])
            return np.dot(np.dot(R.T, A), R)

    def svd_eigen_values(B):
        eig1 = B[0][0] ** 2 + B[0][1] ** 2 + B[1][0] ** 2 + B[1][1] ** 2
        eig2 = B[0][0] * B[1][1] - B[0][1] * B[1][0]
        lambda1 = np.sqrt(eig1 / 2 + np.sqrt((eig1 / 2) ** 2 - eig2))
        lambda2 = np.sqrt(eig1 / 2 - np.sqrt((eig1 / 2) ** 2 - eig2))
        return (lambda1, lambda2)
    A1 = A.copy()
    while True:
        A2 = jacobi_rotation(A1)
        if np.abs(A2 - A1).sum().sum() < 1e-05:
            break
        else:
            A1 = A2
    return tuple(sorted(svd_eigen_values(A2), reverse=True))
