def determinant_4x4(matrix: list[list[int | float]]) -> float:
    if len(matrix) != 4 or len(matrix[0]) != 4:
        raise ValueError('Matrix must be 4x4 for determinant calculation')

    def determinant_3x3(mat):
        return mat[0][0] * (mat[1][1] * mat[2][2] - mat[2][1] * mat[1][2]) - mat[0][1] * (mat[1][0] * mat[2][2] - mat[2][0] * mat[1][2]) + mat[0][2] * (mat[1][0] * mat[2][1] - mat[2][0] * mat[1][1])
    det = 0
    for c in range(4):
        sub_matrix = [[matrix[i][j] for j in range(4) if j != c] for i in range(1, 4)]
        det += (-1) ** c * matrix[0][c] * determinant_3x3(sub_matrix)
    return det
