import numpy as np
def sigmoid(x):
    return 1 / (1 + np.exp(-x))
def derivative_sigmoid(x):
    return x * (1.0 - x)
def train_neuron(features, labels, initial_weights, initial_bias, learning_rate, epochs):
    weights = np.array(initial_weights)
    bias = initial_bias
    mse_values = []
    for _ in range(epochs):
        outputs = sigmoid(np.dot(features, weights) + bias)
        error = labels - outputs
        gradient_weights = learning_rate * (2 / len(features)) * np.dot(features.T, error * derivative_sigmoid(outputs))
        gradient_bias = learning_rate * (2 / len(features)) * np.sum(error * derivative_sigmoid(outputs))
        weights += gradient_weights
        bias += gradient_bias
        mse = np.mean(np.square(error))
        mse_values.append(round(mse, 4))
    return (weights.tolist(), round(bias, 4), mse_values)
