def calculate_brightness(img):
    if not img:
        return -1
    total_sum = 0
    total_pixels = 0
    row_length = len(img[0])
    for row in img:
        if len(row) != row_length:
            return -1
        for pixel in row:
            if pixel < 0 or pixel > 255:
                return -1
            total_sum += pixel
            total_pixels += 1
    return round(total_sum / total_pixels, 2)
