from collections import Counter
def normalize(word):
    return word.lower()
def get_stats(ref_list, can_list):
    ref_counter = Counter(ref_list)
    can_counter = Counter(can_list)
    match_counter = ref_counter & can_counter
    match_count = sum(match_counter.values())
    ref_count = sum(ref_counter.values())
    can_count = sum(can_counter.values())
    return (match_count, ref_count, can_count)
def precision(match_count, can_count):
    if can_count == 0:
        return 0.0
    else:
        return match_count / can_count
def recall(match_count, ref_count):
    if ref_count == 0:
        return 0.0
    else:
        return match_count / ref_count
def F_mean(precision, recall, alpha):
    if precision == 0 and recall == 0:
        return 0.0
    else:
        return (1 + alpha ** 2) * (precision * recall) / (alpha ** 2 * precision + recall)
def penalty(chunks, length):
    if length <= 1:
        return 0.0
    elif length <= 5:
        return 5
    else:
        return 1 / length
def meteor_score(reference, candidate, alpha=0.9, beta=3, gamma=0.5):
    ref_list = [normalize(word) for word in reference.split()]
    can_list = [normalize(word) for word in candidate.split()]
    (match_count, ref_count, can_count) = get_stats(ref_list, can_list)
    P = precision(match_count, can_count)
    R = recall(match_count, ref_count)
    F = F_mean(P, R, alpha)
    Pen = penalty(min(len(ref_list), len(can_list)), beta)
    meteor = P * R / ((1 - gamma) * P + gamma * R) * (1 - Pen)
    return round(meteor, 3)
assert meteor_score('The dog barks at the moon', 'The dog barks at the moon') == 0.998
