def cramers_rule(A, b):
    if np.linalg.det(A) == 0:
        return -1
    n = A.shape[0]
    cofactors = np.zeros((n, n))
    for i in range(n):
        for j in range(n):
            sub_matrix = np.delete(np.delete(A, i, 0), j, 1)
            cofactors[i, j] = (-1) ** (i + j) * np.linalg.det(sub_matrix)
    adj = cofactors.T
    x = np.matmul(np.linalg.inv(A), b)
    solution = np.zeros(n)
    for i in range(n):
        solution[i] = np.matmul(adj[i], b) / np.linalg.det(A)
    return np.around(solution, 4).tolist()
