def determinant_4x4(matrix: list[list[int | float]]) -> float:

    def determinant_3x3(mat):
        return mat[0][0] * (mat[1][1] * mat[2][2] - mat[2][1] * mat[1][2]) - mat[0][1] * (mat[1][0] * mat[2][2] - mat[2][0] * mat[1][2]) + mat[0][2] * (mat[1][0] * mat[2][1] - mat[2][0] * mat[1][1])
    det = 0
    for c in range(4):
        M = matrix[1:]
        for i in range(len(M)):
            M[i] = M[i][0:c] + M[i][c + 1:]
        sign = -1 if c % 2 else 1
        sub_det = determinant_3x3(M)
        det += sign * matrix[0][c] * sub_det
    return det
