from collections import Counter
def entropy(data):
    total = len(data)
    counter = Counter((item[target_attr] for item in data))
    return -sum((counter[k] / total * math.log2(counter[k] / total + (1e-09 if counter[k] / total == 0 else 0)) for k in counter.keys()))
def information_gain(data, attribute):
    total = len(data)
    counter = Counter((item[attribute] for item in data))
    remainder = sum((counter[k] / total * entropy([item for item in data if item[attribute] == k]) for k in counter.keys()))
    return entropy(data) - remainder
def choose_attribute(data, attributes):
    gains = [(attr, information_gain(data, attr)) for attr in attributes]
    return max(gains, key=lambda x: x[1])[0]
def build_tree(data, attributes, default, tree=None):
    if len(attributes) == 0 or len(Counter([record[target_attr] for record in data])) <= 1:
        return default
    else:
        best = choose_attribute(data, attributes)
        tree = {best: {}}
        attributes_left = [attr for attr in attributes if attr != best]
        default = Counter([record[target_attr] for record in data])
        default = max(default, key=default.get)
        for value in set([record[best] for record in data]):
            subdata = [record for record in data if record[best] == value]
            subtree = build_tree(subdata, attributes_left, default)
            tree[best][value] = subtree
        return tree
def learn_decision_tree(examples: list[dict], attributes: list[str], target_attr: str) -> dict:
    tree = build_tree(examples, attributes, Counter([record[target_attr] for record in examples]))
    return tree
