def pegasos_kernel_svm(data, labels, kernel='linear', lambda_val=0.01, iterations=100, sigma=1.0):
    labels = np.where(labels == 0, -1, 1)
    (n_samples, n_features) = data.shape
    alpha = np.zeros(n_samples)
    bias = 0.0
    if kernel == 'linear':
        kernel_matrix = np.dot(data, data.T)
    elif kernel == 'RBF':
        sq_norms = np.sum(data ** 2, axis=1).reshape(-1, 1)
        pairwise_distances = sq_norms + sq_norms.T - 2 * np.dot(data, data.T)
        kernel_matrix = np.exp(-pairwise_distances / (2 * sigma ** 2))
    else:
        raise ValueError('Unknown kernel type: {}'.format(kernel))
    for _ in range(iterations):
        for i in range(n_samples):
            output = labels[i] * (np.dot(kernel_matrix[i], alpha) + bias)
            if output < 1:
                alpha[i] += labels[i] / (lambda_val * n_samples)
                bias += labels[i] / (lambda_val * n_samples)
    return (alpha.tolist(), np.round(bias, 4))
