import numpy as np
import numpy as np
def sigmoid(x):
    return 1 / (1 + np.exp(-x))
def derivative_sigmoid(x):
    return x * (1 - x)
def mean_squared_error(predictions, labels):
    return np.mean((predictions - labels) ** 2)
def train_neuron(features: np.ndarray, labels: np.ndarray, initial_weights: np.ndarray, initial_bias: float, learning_rate: float, epochs: int) -> (np.ndarray, float, list[float]):
    weights = initial_weights
    bias = initial_bias
    mse_values = []
    for _ in range(epochs):
        weighted_sum = np.dot(features, weights) + bias
        predictions = sigmoid(weighted_sum)
        mse = mean_squared_error(predictions, labels)
        mse_values.append(round(mse, 4))
        error = predictions - labels
        dcost_dpred = error
        dpred_dz = derivative_sigmoid(predictions)
        derivative_z = dcost_dpred * dpred_dz
        inputs = features.T
        weights -= learning_rate * np.dot(inputs, derivative_z)
        for i in derivative_z:
            bias -= learning_rate * i
    return (weights.tolist(), round(bias, 4), mse_values)
