def gaussian_elimination(A, b):
    n = len(A)
    aug_A = np.concatenate((A, np.array([b]).T), axis=1)
    for i in range(n):
        max_row = abs(aug_A[i:, i]).argmax() + i
        if i != max_row:
            aug_A[[max_row, i]] = aug_A[[i, max_row]]
        for r in range(i + 1, n):
            f = aug_A[r, i] / aug_A[i, i]
            for c in range(i, n + 1):
                aug_A[r, c] = aug_A[r, c] - aug_A[i, c] * f
    x = np.zeros(n)
    for i in range(n - 1, -1, -1):
        x[i] = (aug_A[i, -1] - np.dot(aug_A[i, :-1], x)) / aug_A[i, i]
    return np.round(x.tolist(), 4)
