from collections import defaultdict
def compute_tf_idf(corpus, query):
    if not corpus:
        return []
    tf_idf_scores = []
    term_freqs = [defaultdict(int) for _ in corpus]
    for i in range(len(corpus)):
        for word in corpus[i]:
            term_freqs[i][word] += 1
    doc_freqs = defaultdict(int)
    for word in set((word for doc in corpus for word in doc)):
        for doc in corpus:
            if word in doc:
                doc_freqs[word] += 1
    max_freqs = [max(term_freqs[i].values()) for i in range(len(corpus))]
    for i in range(len(corpus)):
        for word in corpus[i]:
            term_freqs[i][word] /= max_freqs[i]
    idf = {word: np.log((1 + len(corpus)) / (1 + df)) + 1 for (word, df) in doc_freqs.items()}
    for doc in term_freqs:
        tf_idf = []
        for word in query:
            if word in doc:
                tf_idf.append(round(doc[word] * idf[word], 4))
            else:
                tf_idf.append(0)
        tf_idf_scores.append(tf_idf)
    return np.array(tf_idf_scores).tolist()
