def calculate_brightness(img):
    try:
        if not img:
            return -1
    except TypeError:
        return -1
    row_len = len(img[0])
    for row in img:
        if len(row) != row_len:
            return -1
    total_brightness = 0
    pixel_count = 0
    for row in img:
        for pixel in row:
            if 0 <= pixel <= 255:
                total_brightness += pixel
                pixel_count += 1
            else:
                return -1
    average_brightness = total_brightness / pixel_count
    return round(average_brightness, 2)
