def rmse(y_true, y_pred):
    if not isinstance(y_true, (list, np.ndarray)) or not isinstance(y_pred, (list, np.ndarray)):
        raise ValueError('Input values must be either a list or a numpy array.')
    if isinstance(y_true, list):
        y_true = np.array(y_true)
    if isinstance(y_pred, list):
        y_pred = np.array(y_pred)
    if y_true.shape != y_pred.shape:
        raise ValueError('Input arrays must have the same shape.')
    if y_true.size == 0:
        raise ValueError('Input arrays cannot be empty.')
    rmse_value = np.sqrt(np.mean((y_true - y_pred) ** 2))
    return round(rmse_value, 3)
