def cosine_similarity(v1, v2):
    if isinstance(v1, np.ndarray) and isinstance(v2, np.ndarray):
        v1 = v1.flatten()
        v2 = v2.flatten()
        if v1.size != 0 and v2.size != 0 and (np.linalg.norm(v1) != 0) and (np.linalg.norm(v2) != 0):
            if v1.shape == v2.shape:
                similarity = np.dot(v1, v2) / (np.linalg.norm(v1) * np.linalg.norm(v2))
                return round(similarity, 3)
            else:
                raise ValueError('The input vectors must have the same shape!')
        else:
            raise ValueError('Input vectors cannot be empty or have zero magnitude!')
    else:
        raise TypeError('Inputs must be Numpy arrays!')
