def get_document_statistics(corpus):
    return [len(c) for c in corpus]
def get_avg_document_length(document_lengths):
    return sum(document_lengths) / len(document_lengths)
def get_term_freqs(corpus):
    term_freqs = []
    for document in corpus:
        frequencies = {}
        for term in document:
            if term not in frequencies:
                frequencies[term] = 0
            frequencies[term] += 1
        term_freqs.append(frequencies)
    return term_freqs
def calculate_bm25_scores(corpus, query, k1=1.5, b=0.75):
    document_lengths = get_document_statistics(corpus)
    avg_document_length = get_avg_document_length(document_lengths)
    term_freqs = get_term_freqs(corpus)
    scores = []
    for query_term in query:
        query_term_freqs = [term_freqs[i].get(query_term, 0) for i in range(len(corpus))]
        for i in range(len(corpus)):
            freq = term_freqs[i].get(query_term, 0)
            numerator = freq * (k1 + 1)
            denominator = freq + k1 * (1 - b + b * (document_lengths[i] / avg_document_length))
            if i >= len(scores):
                scores.append(numerator / denominator)
            else:
                scores[i] += numerator / denominator
    scores = [round(score, 3) for score in scores]
    return scores
