import math
def softplus(x: float) -> float:
    """
    Compute the softplus activation function.

    Args:
        x: Input value

    Returns:
        The softplus value: log(1 + e^x)
    """
    try:
        if x > 100:
            return x
        elif x < -100:
            return math.exp(x)
        else:
            return round(math.log(1 + math.exp(x)), 4)
    except Exception as e:
        print(f'An error occurred: {e}')
