import numpy as np
def glm_links():
    """Construct and return standard GLM link functions.

    Returns
    -------
    dict
        A three-entry dictionary (identity, log, logit) where each entry is a
        dictionary containing callables for the link, its inverse and its
        derivative with respect to μ. All functions must work with numpy
        scalars as well as 1-D/2-D numpy arrays via element-wise operations.
    """
    identity = {'link': np.vectorize(lambda mu: mu), 'inv_link': np.vectorize(lambda eta: eta), 'derivative': np.vectorize(lambda mu: 1)}
    log = {'link': np.vectorize(lambda mu: np.log(mu)), 'inv_link': np.vectorize(lambda eta: np.exp(eta)), 'derivative': np.vectorize(lambda mu: 1 / mu)}
    logit = {'link': np.vectorize(lambda mu: np.log(mu / (1 - mu))), 'inv_link': np.vectorize(lambda eta: 1 / (1 + np.exp(-eta))), 'derivative': np.vectorize(lambda mu: 1 / (mu * (1 - mu)))}
    return {'identity': identity, 'log': log, 'logit': logit}

