def lasso_regression(X: list[float] | 'np.ndarray', y: list[float] | 'np.ndarray', degree: int, reg_factor: float, n_iterations: int=1000, tol: float=1e-06) -> list[float]:
    X = np.array(X)
    y = np.array(y)
    X_design = np.empty((X.shape[0], degree + 1))
    X_design[:, 0] = 1
    for i in range(1, degree + 1):
        X_design[:, i] = X ** i
    weights = np.zeros(degree + 1)
    for _ in range(n_iterations):
        weights_prev = weights.copy()
        weights[0] = np.mean(y - np.dot(X_design[:, 1:], weights[1:]))
        for j in range(1, degree + 1):
            rho = np.dot(X_design[:, j], y - (np.dot(X_design, weights) - weights[j] * X_design[:, j]))
            weights[j] = soft_threshold(rho, reg_factor) / np.dot(X_design[:, j], X_design[:, j])
        if np.max(np.abs(weights - weights_prev)) < tol:
            break
    weights = [round(w, 4) for w in weights]
    return weights
def soft_threshold(rho: float, lambda_: float) -> float:
    """Compute the soft threshold function."""
    if rho < -lambda_:
        return rho + lambda_
    elif rho > lambda_:
        return rho - lambda_
    else:
        return 0

