import numpy as np
def metric(name: str):

    def euclidean(x, y):
        (x, y) = (np.array(x), np.array(y))
        return round(np.sqrt(np.sum((x - y) ** 2)), 4)

    def manhattan(x, y):
        (x, y) = (np.array(x), np.array(y))
        return round(np.sum(np.abs(x - y)), 4)

    def chebyshev(x, y):
        (x, y) = (np.array(x), np.array(y))
        return round(np.max(np.abs(x - y)), 4)

    def cosine(x, y):
        (x, y) = (np.array(x), np.array(y))
        (norm_x, norm_y) = (np.linalg.norm(x), np.linalg.norm(y))
        if norm_x == 0 or norm_y == 0:
            return -1
        return round(1 - np.dot(x, y) / (norm_x * norm_y), 4)
    metrics = {'euclidean': euclidean, 'manhattan': manhattan, 'chebyshev': chebyshev, 'cosine': cosine}

    def d(x, y):
        if not (isinstance(x, (list, tuple, np.ndarray)) and isinstance(y, (list, tuple, np.ndarray))):
            return -1
        try:
            (x, y) = (np.array(x, dtype=float), np.array(y, dtype=float))
        except ValueError:
            return -1
        if x.ndim != 1 or y.ndim != 1 or x.size != y.size:
            return -1
        return metrics.get(name, lambda *args: -1)(x, y)
    return d

def blackjack_outcome(player: list[int], dealer: list[int]) -> float:
    """Evaluate the outcome of a finished round of Blackjack.

    The function **must** follow the rules presented in the task description.

    Args:
        player: List of integers (1–10) representing the player's final hand. 1 is Ace.
        dealer: List of integers (1–10) representing the dealer's final hand.

    Returns:
        The player's reward as a float. Possible values are -1, 0, 1 or 1.5.
    """

    def calculate_score(hand):
        total = sum(hand)
        if 1 in hand and total + 10 <= 21:
            total += 10
        return total if total <= 21 else -1
    player_score = calculate_score(player)
    dealer_score = calculate_score(dealer)
    if player_score == -1:
        return -1
    elif dealer_score == -1:
        return 1
    elif player_score == 21 and len(player) == 2:
        return 1.5
    elif player_score > dealer_score:
        return 1
    elif player_score < dealer_score:
        return -1
    else:
        return 0

import numpy as np
def average_pool2d(X: np.ndarray, pool_shape: tuple[int, int], stride: tuple[int, int], accum_grad: np.ndarray) -> tuple[list, list]:
    """Performs forward and backward passes of a 2-D average-pooling layer.

    The function must return a tuple (pooled_output, grad_input), both rounded
    to the nearest 4-th decimal and converted to regular Python lists using
    `tolist()`.
    """
    (N, C, H, W) = X.shape
    (p_h, p_w) = pool_shape
    (s_h, s_w) = stride
    out_h = (H - p_h) // s_h + 1
    out_w = (W - p_w) // s_w + 1
    output = np.zeros((N, C, out_h, out_w))
    grad_input = np.zeros_like(X)
    for n in range(N):
        for c in range(C):
            for h in range(out_h):
                for w in range(out_w):
                    window = X[n, c, h * s_h:h * s_h + p_h, w * s_w:w * s_w + p_w]
                    output[n, c, h, w] = np.mean(window)
    for n in range(N):
        for c in range(C):
            for h in range(out_h):
                for w in range(out_w):
                    window_grad = accum_grad[n, c, h, w] / (p_h * p_w)
                    grad_input[n, c, h * s_h:h * s_h + p_h, w * s_w:w * s_w + p_w] += window_grad
    output = np.round(output, 4).tolist()
    grad_input = np.round(grad_input, 4).tolist()
    return (output, grad_input)

