class GBDT:

    def __init__(self, n_estimators=10, learning_rate=0.1, max_depth=3):
        self.n_estimators = n_estimators
        self.learning_rate = learning_rate
        self.max_depth = max_depth
        self.trees = []

    def fit(self, X, y):
        self.mean = np.mean(y)
        self.trees = []
        residuals = y - self.mean
        for _ in range(self.n_estimators):
            tree = self._build_tree(X, residuals)
            self.trees.append(tree)
            prediction = self._predict_tree(tree, X)
            residuals -= self.learning_rate * prediction

    def predict(self, X):
        y_pred = np.full(len(X), self.mean)
        for tree in self.trees:
            y_pred += self.learning_rate * self._predict_tree(tree, X)
        return y_pred

    def _build_tree(self, X, y, depth=0):
        (n_samples, n_features) = X.shape
        (best_feature, best_threshold, min_error) = (None, None, np.inf)
        for feature in range(n_features):
            thresholds = np.unique(X[:, feature])
            for threshold in thresholds:
                left_idx = X[:, feature] < threshold
                right_idx = X[:, feature] >= threshold
                left_error = np.mean((y[left_idx] - np.mean(y[left_idx])) ** 2)
                right_error = np.mean((y[right_idx] - np.mean(y[right_idx])) ** 2)
                error = left_error + right_error
                if error < min_error:
                    min_error = error
                    best_feature = feature
                    best_threshold = threshold
        if depth < self.max_depth and min_error > 0:
            left_idx = X[:, best_feature] < best_threshold
            right_idx = X[:, best_feature] >= best_threshold
            left = self._build_tree(X[left_idx], y[left_idx], depth + 1)
            right = self._build_tree(X[right_idx], y[right_idx], depth + 1)
            return Node(best_feature, best_threshold, left, right)
        else:
            return Node(value=np.mean(y))

    def _predict_tree(self, tree, X):
        if tree.value is not None:
            return np.full(len(X), tree.value)
        elif X[:, tree.feature] < tree.threshold:
            return self._predict_tree(tree.left, X)
