def apriori_frequent_itemsets(transactions: list[list[int]], min_sup: float) -> list[tuple]:
    """Find frequent itemsets with the Apriori algorithm.

    Parameters
    ----------
    transactions : list[list[Hashable]]
        List of transactions; each transaction is itself a list of items.
    min_sup : float
        Minimum support threshold expressed as a fraction (>0 and ≤1).

    Returns
    -------
    list[tuple]
        Frequent itemsets ordered by length and then lexicographically.
    """
    num_transactions = len(transactions)
    items = set()
    for transaction in transactions:
        for item in transaction:
            items.add(item)
    candidates = list(map(frozenset, [[item] for item in items]))
    frequent_itemsets = []
    k = 1
    while len(candidates) > 0:
        candidate_counts = {}
        for candidate in candidates:
            for transaction in transactions:
                if candidate.issubset(transaction):
                    if candidate in candidate_counts:
                        candidate_counts[candidate] += 1
                    else:
                        candidate_counts[candidate] = 1
        frequent_candidates = []
        for (candidate, count) in candidate_counts.items():
            if count / num_transactions >= min_sup:
                frequent_candidates.append(candidate)
                frequent_itemsets.append(tuple(sorted(candidate)))
        new_candidates = []
        for candidate1 in frequent_candidates:
            for candidate2 in frequent_candidates:
                new_candidate = candidate1.union(candidate2)
                if len(new_candidate) == k + 1 and new_candidate not in new_candidates:
                    new_candidates.append(new_candidate)
        candidates = new_candidates
        k += 1
    frequent_itemsets.sort(key=lambda x: (len(x), x))
    return frequent_itemsets

