import math
def detect_outliers(data: np.ndarray, n_trees: int, sample_size: int, epsilon: float) -> list[int]:
    """Tiny Isolation Forest implementation.

    Args:
        data: 2-D NumPy array where each row is an observation and each column a feature.
        n_trees: Number of random isolation trees that will be grown.
        sample_size: Number of samples drawn (without replacement) to grow every tree.
        epsilon: Fraction (0 ≤ ε < 1) of observations that are considered outliers.

    Returns:
        A list with the zero-based indices of the detected outliers, sorted increasingly.
    """
    (n_samples, n_features) = data.shape
    phi = 2 * math.log(sample_size) - 2 * (sample_size - 1) / sample_size
    height_limit = math.ceil(math.log2(sample_size))

    def c(n):
        if n == 1:
            return 0
        else:
            return 2 * math.log(n - 1) + 0.5772156649 - 2 * (n - 1) / n

    def build_tree(data, height=0):
        if len(data) == 1 or height == height_limit:
            return {'is_leaf': True, 'size': len(data)}
        else:
            feature = np.random.choice(n_features)
            (min_val, max_val) = (np.min(data[:, feature]), np.max(data[:, feature]))
            threshold = np.random.uniform(min_val, max_val)
            left_data = data[data[:, feature] < threshold]
            right_data = data[data[:, feature] >= threshold]
            return {'is_leaf': False, 'feature': feature, 'threshold': threshold, 'left': build_tree(left_data, height + 1), 'right': build_tree(right_data, height + 1)}

    def path_length(tree, x, height=0):
        if tree['is_leaf']:
            return c(tree['size'])
        elif x[tree['feature']] < tree['threshold']:
            return path_length(tree['left'], x, height + 1)
        else:
            return path_length(tree['right'], x, height + 1)
    trees = [build_tree(data[np.random.choice(n_samples, sample_size, replace=False)]) for _ in range(n_trees)]
    scores = np.array([np.mean([path_length(tree, x) for tree in trees]) for x in data])
    anomaly_scores = 2 ** (-scores / phi)
    outlier_threshold = np.percentile(anomaly_scores, 100 * (1 - epsilon))
    outlier_indices = np.where(anomaly_scores > outlier_threshold)[0]
    return sorted(outlier_indices.tolist())

