def maxent_predict(X_train: np.ndarray, y_train: np.ndarray, X_test: np.ndarray, epsilon: float=0.001, n_iter: int=100) -> list[int]:
    empirical_distributions = defaultdict(lambda : defaultdict(int))
    for (x, y) in zip(X_train, y_train):
        empirical_distributions[y][tuple(x)] += 1
    weights = np.zeros(len(empirical_distributions))
    for _ in range(n_iter):
        expected_counts = np.zeros_like(weights)
        for (y, distribution) in empirical_distributions.items():
            for (x, count) in distribution.items():
                expected_counts[y] += count * math.exp(np.dot(weights, x))
        model_distribution = expected_counts / np.sum(expected_counts)
        gradient = model_distribution - empirical_distributions[1] / np.sum(empirical_distributions[1])
        new_weights = weights + gradient
        if np.max(np.abs(new_weights - weights)) < epsilon:
            break
        weights = new_weights
    predictions = []
    for x in X_test:
        log_probabilities = np.zeros(len(weights))
        for (y, distribution) in empirical_distributions.items():
            for (x, count) in distribution.items():
                log_probabilities[y] += count * np.dot(weights, x)
        predictions.append(np.argmax(log_probabilities))
    return predictions

