def elastic_net_regression(X: np.ndarray, y: np.ndarray, learning_rate: float, iterations: int, l1_penalty: float, l2_penalty: float) -> tuple[list[float], float]:
    (m, n) = X.shape
    w = np.zeros(n)
    b = 0.0
    if learning_rate == 0 or iterations == 0:
        return (w.tolist(), round(b, 4))
    for _ in range(iterations):
        y_pred = np.dot(X, w) + b
        residuals = y - y_pred
        dw = (-2 * np.dot(X.T, residuals) + l1_penalty * np.sign(w) + 2 * l2_penalty * w) / m
        db = -2 * np.sum(residuals) / m
        w -= learning_rate * dw
        b -= learning_rate * db
    return ([round(weight, 4) for weight in w], round(b, 4))

