    """Implements the SoftPlus activation function and its derivative"""
    if deriv:
        return np.round(1 / (1 + np.exp(-np.array(x))), 4).tolist()
    else:
        return np.round(np.log1p(np.exp(np.array(x))), 4).tolist()

