def soft_threshold(rho, lambda_):
    if rho < -lambda_:
        return rho + lambda_
    elif rho > lambda_:
        return rho - lambda_
    else:
        return 0
def lasso_regression(X, y, degree, reg_factor, n_iterations=1000, tol=1e-06):
    X = np.array(X)
    y = np.array(y)
    X_poly = np.zeros((X.shape[0], degree + 1))
    X_poly[:, 0] = 1
    for i in range(1, degree + 1):
        X_poly[:, i] = X ** i
    weights = np.zeros(degree + 1)
    for _ in range(n_iterations):
        old_weights = weights.copy()
        weights[0] = np.mean(y - np.dot(X_poly[:, 1:], weights[1:]))
        for j in range(1, degree + 1):
            rho = np.dot(X_poly[:, j], y - np.dot(X_poly, weights) + weights[j] * X_poly[:, j])
            if np.sum(np.abs(old_weights - weights)) < tol:
                break
            if np.sum(X_poly[:, j] ** 2) != 0:
                weights[j] = soft_threshold(rho, reg_factor) / np.sum(X_poly[:, j] ** 2)
            else:
                weights[j] = 0
    return [round(weight, 4) for weight in weights]

from collections import Counter, defaultdict
def fp_growth(transactions: list[list[str]], min_support: int) -> list[list[str]]:
    """Discover every frequent item-set in *transactions* with FP-Growth.

    A *transaction* is represented by a list of items (strings).  `min_support`
    is the minimum number of transactions an item-set has to appear in.  The
    function returns **all** frequent item-sets where
        support(itemset) >= min_support.

    The result must be deterministic:
      • Inside each item-set the items have to be sorted alphabetically.
      • The outer list has to be sorted by `(len(itemset), itemset)`.
    If *transactions* is empty or no item-set meets the threshold return an
    empty list.
    """
    item_counts = Counter()
    for transaction in transactions:
        item_counts.update(transaction)
    items = [[item, count] for (item, count) in item_counts.items() if count >= min_support]
    items.sort(key=lambda x: x[1], reverse=True)
    items = [item[0] for item in items]
    fp_tree = FPTree(transactions, items)
    if fp_tree.is_empty():
        return []
    frequent_itemsets = []
    fp_tree.mine_frequent_itemsets(frequent_itemsets, items, min_support)
    frequent_itemsets.sort(key=lambda x: (len(x), x))
    return frequent_itemsets
class FPTree:

    def __init__(self, transactions, items):
        self.root = FPNode(None, None)
        for transaction in transactions:
            sorted_transaction = [item for item in transaction if item in items]
            sorted_transaction.sort(key=lambda x: items.index(x))
            self.root.add(sorted_transaction)

    def is_empty(self):
        return len(self.root.children) == 0

    def mine_frequent_itemsets(self, frequent_itemsets, items, min_support):
        items = [item for item in items if self.root.count_item(item) >= min_support]
        for item in items:
            frequent_itemsets.append([item])
            conditional_tree = self.conditional_tree(item)
            if not conditional_tree.is_empty():
                conditional_tree.mine_frequent_itemsets(frequent_itemsets, items, min_support)
                self.merge_frequent_itemsets(item, frequent_itemsets)

    def conditional_tree(self, item):
        conditional_tree = FPTree((), [])
        for node in self.root.children.values():
            path = node.get_path_to(item)
            if path is not None:
                path.sort(key=lambda x: items.index(x))
                conditional_tree.root.add(path)
        return conditional_tree

    def merge_frequent_itemsets(self, item, frequent_itemsets):
        for i in range(len(frequent_itemsets)):
            if item in frequent_itemsets[i]:
                for j in range(i + 1, len(frequent_itemsets)):
                    if frequent_itemsets[j][:len(frequent_itemsets[i])] == frequent_itemsets[i]:
                        frequent_itemsets[i].append(frequent_itemsets[j][-1])
                        frequent_itemsets[j] = None
        frequent_itemsets[:] = [itemset for itemset in frequent_itemsets if itemset is not None]
class FPNode:

    def __init__(self, item, parent):
        self.item = item
        self.parent = parent
        self.children = {}
        self.count = 1
        self.next = None

    def add(self, transaction):
        if len(transaction) == 0:
            return
        item = transaction[0]
        if item in self.children:
            node = self.children[item]
            node.count += 1
        else:
            node = self.children[item] = FPNode(item, self)
            if self.next is not None:
                node.next = self.next.add(transaction[1:])
        return node

    def get_path_to(self, item):
        path = []
        node = self
        while node.item is not None:
            if node.item != item:
                path.append(node.item)
            node = node.parent
        path.reverse()
        return path

    def count_item(self, item):
        count = self.count if self.item == item else 0
        for node in self.children.values():
            count += node.count_item(item)
        return count

