from collections import deque
def escape_fire_maze(grid: list[str]) -> int:
    n = len(grid)
    directions = [(0, 1), (0, -1), (1, 0), (-1, 0)]
    fire_queue = deque()
    agent_queue = deque([(0, 0)])
    visited = [[[False] * n for _ in range(n)] for _ in range(n)]
    for i in range(n):
        for j in range(n):
            if grid[i][j] == 'F':
                fire_queue.append((i, j, 0))
                visited[0][i][j] = True
    if grid[0][0] == 'F' or grid[n - 1][n - 1] == 'F':
        return -1
    time = 0
    while agent_queue:
        for _ in range(len(agent_queue)):
            (x, y) = agent_queue.popleft()
            if x == n - 1 and y == n - 1:
                return time
            for (dx, dy) in directions:
                (nx, ny) = (x + dx, y + dy)
                if nx < 0 or ny < 0 or nx >= n or (ny >= n) or (grid[nx][ny] == '#') or visited[time][nx][ny]:
                    continue
                if not visited[time + 1][nx][ny]:
                    agent_queue.append((nx, ny))
                    visited[time + 1][nx][ny] = True
        for _ in range(len(fire_queue)):
            (x, y, t) = fire_queue.popleft()
            for (dx, dy) in directions:
                (nx, ny) = (x + dx, y + dy)
                if nx < 0 or ny < 0 or nx >= n or (ny >= n) or (grid[nx][ny] == '#'):
                    continue
                if not visited[t + 1][nx][ny]:
                    fire_queue.append((nx, ny, t + 1))
                    visited[t + 1][nx][ny] = True
        time += 1
    return -1

