import numpy as np
def mel_filterbank(N: int, n_filters: int=20, fs: int=44000, min_freq: int=0, max_freq: int | None=None, normalize: bool=True) -> list[list[float]]:
    """Build a Mel filterbank transformation matrix.

    The returned matrix has *n_filters* rows and *N//2 + 1* columns.  Each row
    is a triangular filter defined on the Mel scale.  See the detailed task
    description for the exact algorithm that has to be implemented.
    """
    if max_freq is None:
        max_freq = fs // 2
    min_mel = 2595 * np.log10(1 + min_freq / 700)
    max_mel = 2595 * np.log10(1 + max_freq / 700)
    mel_points = np.linspace(min_mel, max_mel, n_filters + 2)
    freq_points = 700 * (10 ** (mel_points / 2595) - 1)
    center_freqs = np.zeros((n_filters,))
    for i in range(n_filters):
        center_freqs[i] = freq_points[i] + (freq_points[i + 2] - freq_points[i + 1]) / 2
    filterbank = np.zeros((n_filters, N // 2 + 1))
    for i in range(n_filters):
        for k in range(N // 2 + 1):
            f_k = k * fs / N
            if freq_points[i] <= f_k < center_freqs[i]:
                filterbank[i, k] = (f_k - freq_points[i]) / (center_freqs[i] - freq_points[i])
            elif center_freqs[i] <= f_k <= freq_points[i + 2]:
                filterbank[i, k] = (freq_points[i + 2] - f_k) / (freq_points[i + 2] - center_freqs[i])
    if normalize:
        filterbank *= 2 / (freq_points[2:] - freq_points[:-2])
    return filterbank.tolist()

from collections import Counter
def build_fp_tree(transactions: list[list[str]], min_support: int) -> dict:

    def update_tree(items, node, counter):
        if items:
            if items[0] in node['children']:
                child = node['children'][items[0]]
                child['support'] += 1
            else:
                child = node['children'][items[0]] = {'support': 1, 'children': {}}
            update_tree(items[1:], child, counter)
    counter = Counter((item for sublist in transactions for item in sublist))
    items = sorted([item for item in counter if counter[item] >= min_support], key=lambda x: (-counter[x], x))
    tree = {'support': 0, 'children': {}}
    for transaction in transactions:
        transaction = [item for item in transaction if item in items]
        transaction.sort(key=lambda x: (items.index(x), x))
        update_tree(transaction, tree, counter)
    return tree

