def conv2D(X: 'np.ndarray', W: 'np.ndarray', stride: int, pad, dilation: int=0):
    """Performs a 2-D convolution (cross-correlation).

    Args:
        X: NumPy array of shape ``(n_ex, in_rows, in_cols, in_ch)`` representing the input batch.
        W: NumPy array of shape ``(kernel_rows, kernel_cols, in_ch, out_ch)`` containing the kernels.
        stride: Stride of the convolution.
        pad: Padding specification – integer, tuple or the string ``'same'``.
        dilation: Optional dilation factor. ``0`` corresponds to a normal convolution.

    Returns:
        The convolution result **as a Python list** obtained through ``numpy.ndarray.tolist()``.
    """
    (n_ex, in_rows, in_cols, in_ch) = X.shape
    (kernel_rows, kernel_cols, _, out_ch) = W.shape
    if isinstance(pad, str) and pad == 'same':
        out_rows = int(np.ceil(in_rows / stride))
        out_cols = int(np.ceil(in_cols / stride))
    else:
        if isinstance(pad, int):
            (pr, pc) = (pad, pad)
        elif isinstance(pad, tuple):
            (pr, pc) = pad
        out_rows = int((in_rows + pr * 2 - kernel_rows - (kernel_rows - 1) * (dilation + 1)) / stride + 1)
        out_cols = int((in_cols + pc * 2 - kernel_cols - (kernel_cols - 1) * (dilation + 1)) / stride + 1)
    Y = np.zeros((n_ex, out_rows, out_cols, out_ch))
    if isinstance(pad, int):
        X_pad = np.pad(X, ((0, 0), (pad, pad), (pad, pad), (0, 0)))
    elif isinstance(pad, tuple):
        X_pad = np.pad(X, ((0, 0), (pad[0], pad[0]), (pad[1], pad[1]), (0, 0)))
    elif isinstance(pad, str) and pad == 'same':
        pad_rows = int((out_rows - 1) * stride + kernel_rows - dilation * (kernel_rows - 1))
        pad_cols = int((out_cols - 1) * stride + kernel_cols - dilation * (kernel_cols - 1))
        (pr, pc) = (pad_rows // 2, pad_cols // 2)
        X_pad = np.pad(X, ((0, 0), (pr, pad_rows - pr), (pc, pad_cols - pc), (0, 0)))
    for r in range(out_rows):
        for c in range(out_cols):
            for d in range(dilation + 1):
                for e in range(dilation + 1):
                    X_pad_slice = X_pad[:, r * stride + d:r * stride + d + kernel_rows:dilation + 1, c * stride + e:c * stride + e + kernel_cols:dilation + 1, :]
                    Y[:, r, c, :] += np.tensordot(X_pad_slice, W, axes=([1, 2, 3], [0, 1, 2]))
    return Y.tolist()

from typing import Any, Dict, Iterable, Tuple as PyTuple
class Space:
    pass
class Box(Space):

    def __init__(self, low: float, high: float, shape: PyTuple[int, ...]):
        self.low = low
        self.high = high
        self.shape = shape
class Tuple(Space):

    def __init__(self, spaces: Iterable[Space]):
        self.spaces = tuple(spaces)
class Dict(Space):

    def __init__(self, spaces: Dict[str, Space]):
        self.spaces = dict(spaces)
class Env:
    """Tiny environment that only stores two spaces."""

    def __init__(self, action_space: Space, observation_space: Space):
        self.action_space = action_space
        self.observation_space = observation_space
def is_continuous(env: Env, tuple_action: bool, tuple_obs: bool):
    """Determine whether the given environment's spaces are continuous.

    A space is *continuous* if it is an instance of `Box`. For composite spaces
    (`Tuple` or `Dict`) the space is continuous only if **all** its sub-spaces
    are `Box`.

    Args:
        env:          Environment exposing `action_space` and `observation_space`.
        tuple_action: Whether the *action* space is composite.
        tuple_obs:    Whether the *observation* space is composite.

    Returns:
        A tuple `(cont_action, cont_obs)` of booleans.
    """

    def check_continuity(space):
        if isinstance(space, Box):
            return True
        elif isinstance(space, Tuple) or isinstance(space, Dict):
            return all((check_continuity(sub_space) for sub_space in space.spaces.values())) if isinstance(space, Dict) else all((check_continuity(sub_space) for sub_space in space.spaces))
        else:
            return False
    cont_action = check_continuity(env.action_space) if tuple_action else isinstance(env.action_space, Box)
    cont_obs = check_continuity(env.observation_space) if tuple_obs else isinstance(env.observation_space, Box)
    return (cont_action, cont_obs)

