class GBDT:

    def __init__(self, n_estimators=10, learning_rate=0.1, max_depth=3):
        self.n_estimators = n_estimators
        self.learning_rate = learning_rate
        self.max_depth = max_depth
        self.trees = []

    def fit(self, X, y):
        self.trees = []
        y_pred = np.full(np.shape(y), np.mean(y))
        for _ in range(self.n_estimators):
            residuals = y - y_pred
            tree = self._build_tree(X, residuals)
            y_pred += np.multiply(self.learning_rate, self._predict(X, tree))
            self.trees.append(tree)

    def predict(self, X):
        y_pred = np.array([0] * len(X))
        for tree in self.trees:
            y_pred += np.multiply(self.learning_rate, self._predict(X, tree))
        return y_pred

    def _build_tree(self, X, y, depth=0):
        (n_samples, n_features) = X.shape
        (best_idx, best_thr) = self._best_split(X, y)
        if best_idx is None:
            return Node(value=np.mean(y))
        if depth >= self.max_depth:
            return Node(value=np.mean(y))
        (left_idx, right_idx) = (X[:, best_idx] < best_thr, X[:, best_idx] >= best_thr)
        left = self._build_tree(X[left_idx], y[left_idx], depth + 1)
        right = self._build_tree(X[right_idx], y[right_idx], depth + 1)
        return Node(best_idx, best_thr, left, right)

    def _best_split(self, X, y):
        mse = np.mean(y ** 2)
        (best_idx, best_thr, best_mse) = (None, None, mse)
        for idx in range(X.shape[1]):
            (thresholds, classes) = zip(*sorted(zip(X[:, idx], y)))
            num_left = [0]
            num_right = [len(X)]
            score_left = [0]
            score_right = [sum(y ** 2)]
            for i in range(1, len(X)):
                if thresholds[i] == thresholds[i - 1]:
                    continue
                num_left.append(i)
                num_right.append(len(X) - i)
                score_left.append(score_left[-1] + y[i - 1] ** 2)
                score_right.append(score_right[-1] - y[i - 1] ** 2)
            num_left = np.array(num_left[1:])
            num_right = np.array(num_right[1:])
            score_left = np.array(score_left[1:])
            score_right = np.array(score_right[1:])
            mse_left = np.divide(score_left, num_left, out=np.inf * np.ones_like(score_left), where=num_left != 0)
            mse_right = np.divide(score_right, num_right, out=np.inf * np.ones_like(score_right), where=num_right != 0)
            mse_values = np.add(np.multiply(num_left, mse_left), np.multiply(num_right, mse_right))
            if np.min(mse_values) < best_mse:
                best_mse = np.min(mse_values)
                best_idx = idx
                best_thr = (thresholds[np.argmin(mse_values) - 1] + thresholds[np.argmin(mse_values)]) / 2
        return (best_idx, best_thr)

    def _predict(self, X, tree):
        if tree.value is not None:
            return np.full(len(X), tree.value)
        if X[tree.feature, :] < tree.threshold:
            return self._predict(X, tree.left)
        else:
            return self._predict(X, tree.right)
def gbdt_regression(X_train, y_train, X_test, n_estimators=10, learning_rate=0.1, max_depth=3):
    model = GBDT(n_estimators=n_estimators, learning_rate=learning_rate, max_depth=max_depth)
    model.fit(X_train, y_train)
    predictions = model.predict(X_test)
    return [round(pred, 4) for pred in predictions]

import numpy as np
def root_mean_squared_log_error(actual: list[float], predicted: list[float]) -> float:
    """Compute the Root Mean Squared Logarithmic Error (RMSLE)"""
    if len(actual) != len(predicted):
        return -1
    if any((i < 0 for i in actual)) or any((i < 0 for i in predicted)):
        return -1
    rmsle = np.sqrt(np.mean(np.square(np.log1p(predicted) - np.log1p(actual))))
    return round(rmsle, 4)

import itertools
def apriori_frequent_itemsets(transactions: list[list[int]], min_sup: float) -> list[tuple]:
    """Find frequent itemsets with the Apriori algorithm.

    Parameters
    ----------
    transactions : list[list[Hashable]]
        List of transactions; each transaction is itself a list of items.
    min_sup : float
        Minimum support threshold expressed as a fraction (>0 and ≤1).

    Returns
    -------
    list[tuple]
        Frequent itemsets ordered by length and then lexicographically.
    """
    item_counts = {}
    for transaction in transactions:
        for item in transaction:
            if item in item_counts:
                item_counts[item] += 1
            else:
                item_counts[item] = 1
    min_count = min_sup * len(transactions)
    frequent_items = {item: count for (item, count) in item_counts.items() if count >= min_count}
    frequent_itemsets = list(frequent_items.keys())
    k = 2
    while True:
        candidates = list(itertools.combinations(frequent_itemsets, k))
        candidate_counts = {}
        for candidate in candidates:
            for transaction in transactions:
                if all((item in transaction for item in candidate)):
                    if candidate in candidate_counts:
                        candidate_counts[candidate] += 1
                    else:
                        candidate_counts[candidate] = 1
        frequent_candidates = {candidate: count for (candidate, count) in candidate_counts.items() if count >= min_count}
        if not frequent_candidates:
            break
        frequent_itemsets.extend(frequent_candidates.keys())
        k += 1
    frequent_itemsets.sort(key=lambda x: (len(x), x))
    return frequent_itemsets

