def maxent_predict(X_train: np.ndarray, y_train: np.ndarray, X_test: np.ndarray, epsilon: float=0.001, n_iter: int=100) -> list[int]:
    """Fill in here."""
    empirical_distributions = defaultdict(lambda : defaultdict(float))
    for (x, y) in zip(X_train, y_train):
        empirical_distributions[y][tuple(x)] += 1
    weights = np.zeros(X_train.shape[1])
    for _ in range(n_iter):
        weight_updates = np.zeros_like(weights)
        for (x, y) in zip(X_train, y_train):
            exponent_sum = np.sum(weights * x)
            marginal_prob = np.sum([math.exp(weights.dot(np.array(xi == x))) for xi in empirical_distributions[y]])
            weight_updates += x * empirical_distributions[y][tuple(x)] / marginal_prob
        weights += weight_updates / X_train.shape[0]
        if np.max(np.abs(weight_updates)) < epsilon:
            break
    y_pred = []
    for x in X_test:
        log_probs = {y: weights.dot(np.array(x == xi)) for (y, dist) in empirical_distributions.items() for xi in dist}
        y_pred.append(max(log_probs, key=log_probs.get))
    return y_pred

import numpy as np
def local_outlier_factor(data, k, epsilon=1.5):
    n = len(data)
    distances = np.sqrt(np.sum((data[None, :] - data[:, None]) ** 2, -1))
    k_distance = np.zeros(n)
    k_nearest_neighbors = np.zeros((n, k), dtype=int)
    reach_distances = np.zeros((n, n))
    lrd = np.zeros(n)
    lof = np.zeros(n)
    for i in range(n):
        distances_to_i = distances[i]
        k_nearest_neighbors[i] = np.argsort(distances_to_i)[:k]
        k_distance[i] = distances_to_i[k_nearest_neighbors[i][-1]]
    for i in range(n):
        for j in range(n):
            reach_distances[i, j] = max(k_distance[j], distances[i, j])
    for i in range(n):
        lrd[i] = k / np.sum(reach_distances[i, k_nearest_neighbors[i]])
    for i in range(n):
        lof[i] = np.sum(lrd[k_nearest_neighbors[i]] / (k * lrd[i])) / (n - 1)
    lof = np.round(lof, 4)
    outlier_indices = np.where(lof > epsilon)[0]
    return (lof.tolist(), outlier_indices.tolist())
