def unsmoothed_ngram_log_prob(corpus: list[str], sequence: list[str], N: int) -> float:
    ngrams = list(zip(*[corpus[i:] for i in range(N)]))
    ngram_counts = Counter(ngrams)
    sequence_ngrams = list(zip(*[sequence[i:] for i in range(N)]))
    total_log_prob = 0
    for ngram in sequence_ngrams:
        ngram_count = ngram_counts[ngram]
        prefix_count = ngram_counts[ngram[:-1]]
        if ngram_count == 0 or prefix_count == 0:
            return float('-inf')
        prob = ngram_count / prefix_count
        total_log_prob += np.log(prob)
    return round(total_log_prob, 4)

