def lasso_regression(X, y, degree, reg_factor, n_iterations=1000, tol=1e-06):
    X = np.array(X)
    y = np.array(y)
    X_poly = np.column_stack([X ** d for d in range(degree + 1)])
    weights = np.zeros(degree + 1)
    m = len(y)
    for _ in range(n_iterations):
        old_weights = weights.copy()
        weights[0] = np.mean(y - np.dot(X_poly[:, 1:], weights[1:]))
        for j in range(1, degree + 1):
            rho = np.dot(X_poly[:, j], y - np.dot(X_poly[:, :j + 1], weights[:j + 1]) + weights[j] * X_poly[:, j])
            if np.isnan(rho):
                weights[j] = 0
            else:
                weights[j] = soft_threshold(rho, reg_factor) / np.dot(X_poly[:, j], X_poly[:, j])
        max_weight_change = np.max(np.abs(old_weights - weights))
        if max_weight_change < tol:
            break
    return [round(weight, 4) for weight in weights]

