def additive_ngram_log_prob(corpus: list[str], sequence: str, N: int, K: float=1.0) -> float:

    def ngrams(sequence, n):
        sequence = (n - 1) * ['<START>'] + sequence.split()
        return [tuple(sequence[i:i + n]) for i in range(len(sequence) - n + 1)]

    def count_ngrams(corpus, n):
        ngram_counts = defaultdict(Counter)
        for sentence in corpus:
            for ngram in ngrams(sentence, n):
                (context, word) = (ngram[:-1], ngram[-1])
                ngram_counts[context][word] += 1
        return ngram_counts

    def count_contexts(ngram_counts):
        context_counts = Counter()
        for (ngram, counts) in ngram_counts.items():
            context_counts[ngram[:-1]] += sum(counts.values())
        return context_counts

    def vocab_size(ngram_counts):
        return len(set((word for counts in ngram_counts.values() for word in counts))) + 1
    ngram_counts = count_ngrams(corpus, N)
    context_counts = count_contexts(ngram_counts)
    V = vocab_size(ngram_counts)
    sentence_log_prob = 0.0
    for ngram in ngrams(sequence, N):
        (context, word) = (tuple(ngram[:-1]), ngram[-1])
        if word not in ngram_counts[context]:
            word = '<UNK>'
        count = ngram_counts[context][word]
        context_count = context_counts[context]
        prob = (count + K) / (context_count + K * V)
        sentence_log_prob += math.log(prob)
    return round(sentence_log_prob, 4)

